<?php	function newsView() {		$newsQuery = mysql_query("SELECT * FROM t_news WHERE n_id=$_GET[nid]");		$newsRow = mysql_fetch_array($newsQuery);		return $newsRow;	}		function newsEdit() {		$query = mysql_fetch_array(mysql_query("SELECT * FROM t_news WHERE n_id=$_GET[nid]"));		return $query;	}	function newsList($limit, $numberOfNews) {					$STH = $DB->query("SELECT * FROM t_news ORDER BY n_id DESC"); // LIMIT $limit, $numberOfNews		$STH->setFetchMode(PDO::FETCH_ASSOC); 						$newsArr = array();		while($newsRow = $STH->fetch()) {			$newsArr[] = $newsRow;		}		return $newsArr;	}