<div id='mainbody'>	<?php				 if(isset($_GET['page']) and is_numeric($_GET['page'])) {		if($_GET['page'] == 1) { $limit = 0; } else { $limit = ($_GET['page'] - 1) * 5; }	  } else { $limit = 0; }			  if(isset($_GET['delete']) AND $_GET['delete']=='ok') { echo "<div class='ok'>News deleted</div>"; }				$STH = $DB->query("SELECT n.n_id, n.n_title, n.n_title_en, n.n_text, n.n_text_en, n.n_add_user, n.n_add_time, u.u_id, u.u_login FROM t_news n INNER JOIN t_users u ON n.n_add_user=u.u_id ORDER BY n_id DESC  LIMIT $limit, 5");		$STH->setFetchMode(PDO::FETCH_ASSOC); 		while($news = $STH->fetch()) {												echo "<div class='introNews'>					<div class='newsTitle'><a href='news.php?nid=".$news['n_id']."' class='titleLink'>";					if($_SESSION['uLANG'] ==  'en') { echo $news['n_title_en']; } else { echo $news['n_title']; }		    echo "</a></div>										<div class='text'>";					  if($_SESSION['uLANG'] ==  'en') {					  	if(strlen($news['n_text_en'])>150) {							echo substr($news['n_text_en'],0,150).'...';} else { echo $news['n_text_en']; 						}					  } else {						if(strlen($news['n_text'])>150) {							echo substr($news['n_text'],0,150).'...';} else { echo $news['n_text']; 						}					  }					echo "					<a href='news.php?nid=".$news['n_id']."'>".$lang['Read more']."</a><br>					<font color='898989'>".$lang['Author'].": <a href='user.php?id=".$news['u_id']."'>".$news['u_login']."</a> | ".$lang['Date'].": ".$news['n_add_time']."</font>					</div></div>";		}			$STH = $DB->query('SELECT count(*) FROM t_news');			$numpages = $STH->fetch();			$numpages =  ceil($numpages[0]/5);			echo "<div id='pageBtBlock'>";			for($i = 1; $i<=$numpages; $i++) {				if((!isset($_GET['page']) AND $i == 1) 				       OR (isset($_GET['page']) AND $_GET['page'] == $i)) {					echo "<a href='news.php&amp;page=$i' class='pageBtSelected'>$i</a>";				} else {					echo "<a href='news.php?page=$i' class='pageBt'>$i</a>";				}			}			echo "</div>";?><div class='clearfix'></div></div>