<?php	$STH = $DB->query('SELECT COUNT(*) AS count FROM t_users');	$STH->setFetchMode(PDO::FETCH_ASSOC);	$users = $STH->fetch(); ?><div id='mainbody'>	<div class='newsTitle' style='border-top: 1px solid #c7c7c7;'><?=$lang['Users'];?>		<br /> 		<div style='font-size: 11px; color: #898989;'><?=$lang['Register users'];?>: <?=$users['count'];?></div>	</div>	<?php if(isset($_GET['er'])) { echo "<div class='error'>Error</div>"; } ?>		<?php 		if(isset($_GET['st'])) { 			if($_GET['st'] == 'editok') {echo "<div class='ok'>".$lang['SAVED']."</div>";}; 			if($_GET['st'] == 'deleteok') {echo "<div class='ok'>User Delete</div>";}; 		} 	?>	<table border='0' width='100%' class='userListTable'>		<tr>			<td class='userListTitleTD'>ID</td>			<td class='userListTitleTD'><?=$lang['Login'];?></td>			<td class='userListTitleTD'><?=$lang['E-mail'];?></td>			<td class='userListTitleTD'><?=$lang['Sex'];?></td>			<td class='userListTitleTD'><?=$lang['Group'];?></td>		</tr>						<?php								$STH = $DB->query('SELECT * FROM t_users ORDER BY u_login');				$STH->setFetchMode(PDO::FETCH_ASSOC); 				while($usersRow = $STH->fetch()) {					echo "<tr>							<td class='user'>".$usersRow['u_id']." </td>							<td class='user'>								<a href='user.php?id=".$usersRow['u_id']."'>";									if($usersRow['u_group'] == 0) { echo "<font color='red'>".$usersRow['u_login']."</font>"; } else { echo $usersRow['u_login']; }					echo "  	</a>							</td>							<td class='user'>".$usersRow['u_mail']."</td>							<td class='user'>";															if($usersRow['u_sex']==1) {echo $lang['Male'];} else {echo $lang['Female'];}										echo "	</td>								<td class='user'>";								if (isset($_SESSION['uGROUP']) AND $_SESSION['uGROUP'] == 1) {									echo "<form method='post' action='engine/bin/changeUserGroup.php' name='changegroup' style='float: left;'>											<div><select name='uGroup'>												  <option value='0'"; if($usersRow['u_group'] == 0) { echo " selected='selected'>";} else { echo ">";} echo $lang['Blocked']; echo "</option>";									echo "		  <option value='3'"; if($usersRow['u_group'] == 3) { echo " selected='selected'>";} else { echo ">";} echo $lang['User'];	   echo "</option>";									echo "		  <option value='2'"; if($usersRow['u_group'] == 2) { echo " selected='selected'>";} else { echo ">";} echo $lang['Moder'];   echo "</option>";									echo "	 	  <option value='1'"; if($usersRow['u_group'] == 1) { echo " selected='selected'>";} else { echo ">";} echo $lang['Admin'];   echo "</option>";											  										echo"</select>												<input type='hidden' name='userID' value='".$usersRow['u_id']."' />												<input type='submit' value='".$lang['Save']."' /></div>														  </form>";										  									echo "<form method='post' action='engine/bin/deleteUser.php' name='deluser'>											<div><input type='hidden' name='deleteUID' value='".$usersRow['u_id']."' />											<input type='submit' value='".$lang['Delete']."' /></div>										  </form>";								} else {									switch ($usersRow['u_group']) {										case 1: echo $lang['Admin']; break;										case 2: echo $lang['Moder']; break;										case 3: echo $lang['User']; break;										case 0: echo $lang['Blocked']; break;									}								}				}			?>			</td>		</tr>	</table></div>