<div class='newsTitle' style='border-top: 1px solid #c7c7c7;'><?=$lang['New users'];?></div><?php	$STH = $DB->query('SELECT u_id, u_login FROM t_users ORDER BY u_id DESC LIMIT 5');	$STH->setFetchMode(PDO::FETCH_ASSOC);	while($usersRow = $STH->fetch()) {		echo "<div class='user'><a href='user.php?id=".$usersRow['u_id']."'>".$usersRow['u_login']."</a></div>";	}		$STH = $DB->query('SELECT COUNT(*) AS count FROM t_users');	$uCount = $STH->fetch();	echo "<a href='users.php' style='float: right; padding: 3px 6px;'>".$lang['All']."(".$uCount['count'].")</a>";	?>